import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';

import {
  clickedPaymentButton,
  clickedProfileButton,
} from '../../actions/dashboardAction';

class AllProfile extends Component {
  state = {
    dashboardMenu: '',
  };
  componentDidUpdate() {
    renderProfileButton = () => {
      this.setState({ dashboardMenu: 'GET_PROFILE' });
      console.log(this.props);
      console.log(this.state);
      clickedProfileButton('GET_PROFILE');
    };

    renderPaymentButton = () => {
      this.setState({ dashboardMenu: 'GET_PAYMENT' });
      console.log(this.state);
      clickedPaymentButton('GET_PAYMENT');
    };
  }
  render() {
    return (
      <div>
        <button
          onClick={this.renderProfileButton}
          className='bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded-full mb-3'>
          Profile
        </button>
        <button
          onClick={this.renderPaymentButton}
          className='bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded-full'>
          Payment Details
        </button>
      </div>
    );
  }
}

const propTypes = {
  clickedPaymentButton: PropTypes.func.isRequired,
  clickedPaymentButton: PropTypes.func.isRequired,
};

const mapStateToProps = {};

export default connect(null, {
  clickedPaymentButton,
  clickedProfileButton,
})(AllProfile);
