import React, { Component } from 'react';
import { connect } from 'react-redux';
import { reduxForm, Field, formValues } from 'redux-form';
import { Link } from 'react-router-dom';
import PropTypes from 'prop-types';

import { register } from '../../actions';

export class Register extends Component {
  state = {
    firstname: '',
    lastname: '',
    email: '',
    password: '',
    repassword: '',
  };

  onSubmit(formValues) {
    const newUser = {
      firstname: this.state.firstname,
      lastname: this.state.lastname,
      email: this.state.email,
      password: this.state.password,
      repassword: this.state.repassword,
    };
    console.log(newUser);
    register(newUser);
  }

  render() {
    return (
      <div className='bg-green-200'>
        <div
          className='w-full max-w-lg mx-auto flex items-center'
          style={{ height: '100vmin' }}>
          <form
            className='bg-white shadow-md rounded px-8 pt-6 pb-8 mb-4'
            onSubmit={this.props.handleSubmit(this.onSubmit)}>
            <div className='flex flex-wrap -mx-3 mb-4'>
              <label className='block uppercase tracking-wide text-gray-700 text-xs font-bold mb-2" for="grid-first-name'>
                First Name
              </label>
              <input
                className='ppearance-none block w-full bg-gray-200 text-gray-700 border border-red-500 rounded py-3 px-4 mb-3 leading-tight focus:outline-none focus:bg-white'
                type='text'
                name='firnstname'
              />
            </div>

            <p className='text-gray-600 text-xs italic'>Atleast 6 character</p>
            <Field
              name='repassword'
              type='password'
              label='Re-Enter Password'
              component={this.renderInput}
            />
            <div className='flex justify-end'>
              <Link
                to='/login'
                className='mr-3 mt-3 bg-green-700 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded-full'>
                Already Registerd ?
              </Link>
              <button className='mr-3 mt-3 bg-green-700 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded-full'>
                Register
              </button>
            </div>
          </form>
        </div>
      </div>
    );
  }
}

Register.propTypes = {
  register: PropTypes.func.isRequired,
  auth: PropTypes.object.isRequired,
};
const mapStateToProps = (state) => ({
  auth: state.auth,
});

// const mapDispatchToProps = {}

export const RegisterForm = reduxForm({ form: 'registerForm' })(Register);

export default connect(mapStateToProps, { register })(RegisterForm);
