import { connect } from 'react-redux';
import React from 'react';

class Auth {
  constructor() {
    this.authenticated = auth.isAuthenticated;
  }

  isAuthenticated() {
    return this.authenticated;
  }
}

const mapStateToProps = (state) => ({
  auth: state.auth,
});

export default new connect(mapStateToProps, null)(Auth());
