import { GET_ERROR, CLEAR_ERROR } from './types';

export const returnError = (errorMessage, success, id = null) => {
  return {
    type: GET_ERROR,
    payload: { errorMessage, success, id },
  };
};

export const clearError = (errorMessage, success, id = null) => {
  return {
    type: CLEAR_ERROR,
  };
};
