import { REGISTERSUCCESS, REGISTER_FAIL } from '../actions/types';

const initialState = {
  token: localStorage.getItem('token'),
  isRegistered: false,
  user: null,
  msg: '',
};

export default function (state = initialState, action) {
  switch (action.type) {
    case REGISTERSUCCESS:
      return {
        ...state,
        ...action.payload,
        msg: 'User account created Success full',
      };
    case REGISTER_FAIL:
      return {
        ...state,
        ...action.payload,
      };
    default:
      return state;
  }
}
