import React, { Component } from 'react';
import { connect } from 'react-redux';

export class Progress extends Component {
  showProgress = () => {
    if (this.props.user.user.role === 3) {
      return (
        <div className='text-2xl text-center'>Classes will stard on Date</div>
      );
    } else {
      return <div className='text-2xl text-center'>Not Yet Payed</div>;
    }
  };

  render() {
    return (
      <div>
        <div className='text-5xl'>{this.props.user.user.firstname}</div>
        <div>{this.showProgress}</div>
      </div>
    );
  }
}

const mapStateToProps = (state) => ({
  user: state.auth.user,
});

export default connect(mapStateToProps, null)(Progress);
