import React, { Component } from 'react';
import { connect } from 'react-redux';
import { withRouter } from 'react-router-dom';

export class Progress extends Component {
  state = {
    firstname: '',
    role: 2,
  };

  componentDidMount() {
    console.log(this.props);
    this.setState({
      firstname: this.props.user.user.firstname,
      role: this.props.user.user.role,
    });
  }
  render() {
    const { firstname, role } = this.state;
    const payed = (
      <div className='text-2xl text-center'>Classes will start on Date</div>
    );

    const notPayed = (
      <div className='text-3xl text-center'>
        Please Do the Payment for the Course You want to Enroll
      </div>
    );

    return (
      <div>
        <div className='min-h-71'>
          <div className='text-5xl'>Hi, {firstname}</div>
          <div>{role === 3 ? payed : notPayed}</div>
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state) => ({
  user: state.auth.user,
});

export default connect(mapStateToProps, null)(withRouter(Progress));
