import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';

import { googleSignIn, googleSignOut } from '../../actions';

export class GoogleAuth extends Component {
  componentDidMount() {
    window.gapi.load('client:auth2', () => {
      window.gapi.client
        .init({
          clientId:
            '559727615856-1akuiiknf4ecb5moqlvvti8b9h5l8m1j.apps.googleusercontent.com',
          scope: 'email',
        })
        .then(() => {
          this.auth = window.gapi.auth2.getAuthInstance();
          this.onAuthChange(this.auth.isAuthenticated.get());
          this.auth.isAuthenticated.listen(this.onAuthChange);
        });
    });
  }

  onAuthChange = (isAuthenticated) => {
    if (isAuthenticated) {
      this.props.googleSignIn(this.auth.currentUser.get().getId());
    } else {
      this.props.googleSignOut();
    }
  };

  onSignInClick = () => {
    this.auth.googleSignIn();
  };

  onSignOutClick = () => {
    this.auth.googleSignOut();
  };

  renderAuthButton() {
    if (this.props.isSignedIn === null) {
      return null;
    } else if (this.props.isSignedIn) {
      return (
        <div>
          <button
            onClick={this.onSignOutClick}
            className='mr-3 bg-transparent hover:bg-blue-500 text-blue-700 font-semibold hover:text-white py-2 px-4 border border-blue-500 hover:border-transparent rounded'>
            Sign Out
          </button>
        </div>
      );
    } else {
      return (
        <div>
          <button
            onClick={this.onSignInClick}
            className='mr-3 bg-transparent hover:bg-blue-500 text-blue-700 font-semibold hover:text-white py-2 px-4 border border-blue-500 hover:border-transparent rounded'>
            Sign In
          </button>
        </div>
      );
    }
  }

  render() {
    return <div>{this.renderAuthButton()}</div>;
  }
}

const mapStateToProps = (state) => ({
  isAuthenticated: state.auth.isAuthenticated,
});

export default connect(mapStateToProps, { googleSignIn, googleSignOut })(
  GoogleAuth
);
