import React, { useState } from 'react';
import FileUpload from '../utils/FileUpload';

const Continents = [
  { key: 1, value: 'Africa' },
  { key: 2, value: 'Europe' },
  { key: 3, value: 'Asia' },
  { key: 4, value: 'North America' },
  { key: 5, value: 'Australia' },
];

function UploadProductPage(props) {
  const [TitleValue, setTitleValue] = useState('');
  const [DescriptionValue, setDescriptionValue] = useState('');
  const [PriceValue, setPriceValue] = useState(0);
  const [ContinentValue, setContinentValue] = useState(1);

  const [Images, setImages] = useState([]);

  const onTitleChange = (event) => {
    setTitleValue(event.currentTarget.value);
  };

  const onDescriptionChange = (event) => {
    setDescriptionValue(event.currentTarget.value);
  };

  const onPriceChange = (event) => {
    setPriceValue(event.currentTarget.value);
  };

  const onContinentsSelectChange = (event) => {
    setContinentValue(event.currentTarget.value);
  };

  const updateImages = (newImages) => {
    setImages(newImages);
  };
  const onSubmit = (event) => {
    event.preventDefault();

    const variables = {
      writer: props.user.userData._id,
    };
  };

  return (
    <div>
      <div className='container mx-auto m-10'>
        <h2 className='text-4xl text-center font-medium'>
          Upload Travel Product
        </h2>

        <div className='flex justify-center mt-20'>
          <form className='w-full max-w-lg' onSubmit={onSubmit}>
            <FileUpload refreshFunction={updateImages} />
            <div className='flex flex-wrap -mx-3 mb-6 mt-10'>
              <div className='w-full  px-3 mb-6 md:mb-0'>
                <label
                  className='block uppercase tracking-wide text-gray-700 text-xs font-bold mb-2'
                  htmlFor='grid-title'>
                  Title
                </label>
                <input
                  className='appearance-none block w-full bg-gray-200 text-gray-700rounded py-3 px-4 mb-10 leading-tight focus:outline-none focus:bg-white'
                  id='grid-title'
                  onChange={onTitleChange}
                  value={TitleValue}
                  type='text'
                  placeholder='Title'
                />
              </div>
              <div className='w-full mb-6 md:mb-0 px-3'>
                <label
                  className='block uppercase tracking-wide text-gray-700 text-xs font-bold mb-2'
                  htmlFor='grid-description'>
                  Discription
                </label>
                <textarea
                  className='ppearance-none block w-full bg-gray-200 text-gray-700rounded py-3 px-4 mb-10 leading-tight focus:outline-none focus:bg-white'
                  id='grid-description'
                  onChange={onDescriptionChange}
                  value={DescriptionValue}
                  type='text'
                  placeholder='Description'
                />
              </div>
              <div className='w-full mb-6 md:mb-0 px-3'>
                <label
                  className='block uppercase tracking-wide text-gray-700 text-xs font-bold mb-2'
                  htmlFor='grid-price'>
                  Price
                </label>
                <input
                  className='ppearance-none block w-full bg-gray-200 text-gray-700rounded py-3 px-4 mb-10 leading-tight focus:outline-none focus:bg-white'
                  id='grid-price'
                  onChange={onPriceChange}
                  value={PriceValue}
                  type='number'
                  placeholder='200'
                />
              </div>
            </div>
            <select
              onChange={onContinentsSelectChange}
              value={ContinentValue}
              className='block appearance-none w-full bg-white border border-gray-400 hover:border-gray-500 px-4 py-2 pr-8 mb-10 rounded shadow leading-tight focus:outline-none focus:shadow-outline'>
              {Continents.map((item) => (
                <option key={item.key} value={item.key}>
                  {item.value}
                </option>
              ))}
            </select>
            <button
              onSubmit={onSubmit}
              className='float-right bg-gray-500 hover:bg-gray-700 text-white font-bold py-2 px-4 border border-blue-700 rounded'>
              Submit
            </button>
          </form>
        </div>
      </div>
    </div>
  );
}

export default UploadProductPage;
