import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';

import {
  clickedPaymentButton,
  clickedProfileButton,
} from '../../actions/dashboardAction';

class AllProfile extends Component {
  state = {
    dashboard: 1,
  };

  renderButton = (values) => {
    this.setState({ data: values });
  };

  render() {
    if (this.state.dashboard === 1)
      return (
        <div>
          <button className='bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded-full mb-3'>
            Profile
          </button>
        </div>
      );
    else {
      return (
        <div>
          <button
            onClick={this.renderButton('payment')}
            className='bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded-full'>
            Payment Details
          </button>
        </div>
      );
    }
  }
}

export default connect(null, {
  clickedPaymentButton,
  clickedProfileButton,
})(AllProfile);
