import {
  REGISTER_FAIL,
  LOGIN_FAIL,
  LOGIN_SUCCESS,
  SIGN_OUT,
  REGISTERSUCCESS,
  USER_LOADING,
  USER_LOADED,
  AUTH_ERROR,
  GOOGLE_SIGN_IN,
  GOOGLE_SIGN_OUT,
  VERIFICATION_SUCCESS,
  VERIFICATION_FAIL,
  SEND_OTP_SUCCESS,
  SEND_OTP_FAIL,
  DETAIL_UPDATING_SUCCESS,
  DETAIL_UPDATING_FAIL,
  USERS_LOADING_SUCCESS,
  USERS_LOADING_FAIL,
  USER_ACTIVATE_FAIL,
  USER_ACTIVATE_SUCCESS,
} from './types';

import { returnError } from './errorAction';

import Axios from 'axios';
import { getMessage } from './msgAction';
import { clickedPaymentButton } from './dashboardAction';

export const loadUser = () => (dispatch) => {
  dispatch({ type: USER_LOADING });
  Axios.get('/api/users/auth')
    .then((res) => {
      dispatch({
        type: USER_LOADED,
        payload: res.data,
      });
    })
    .catch((err) => {
      console.log(err);
      dispatch(returnError(err.response.data, 'AUTH_ERROR'));
      dispatch({ type: AUTH_ERROR });
    });
};

export const signIn = (userId) => {
  return {
    type: GOOGLE_SIGN_IN,
    payload: userId,
  };
};

export const signOut = () => {
  return {
    type: GOOGLE_SIGN_OUT,
  };
};

export const login = (formValues) => {
  const respose = fetch('/api/users/login', {
    method: 'post',
    headers: {
      'Content-Type': 'application/json',
    },
    body: JSON.stringify(formValues),
  }).then((res) => res.data);
  if (!respose.success) {
    getMessage(respose.message, 'LOGIN_SUCCESS');
    return {
      type: LOGIN_SUCCESS,
      payload: respose,
    };
  } else {
    returnError(respose.errorMessage, 'LOGIN_FAIL');
    return {
      type: LOGIN_FAIL,
    };
  }
};

export const register = (formValues) => {
  return (dispatch) => {
    const config = {
      headers: {
        'Content-Type': 'application/json',
      },
    };

    const body = JSON.stringify(formValues);
    if (formValues.password !== formValues.repassword) {
      dispatch(returnError('Password did not Match', 'REGISTER_FAIL'));
      dispatch({
        type: REGISTER_FAIL,
      });
    } else {
      Axios.post('/api/users/register', body, config)
        .then((res) => {
          dispatch(getMessage(res.data.message, 'REGISTERSUCCESS'));
          dispatch({
            type: REGISTERSUCCESS,
            payload: res.data,
          });
        })
        .catch((err) => {
          dispatch(
            returnError(err.response.data.errorMessage, 'REGISTER_FAIL')
          );
          dispatch({
            type: REGISTER_FAIL,
          });
        });
    }
  };
};

export const logout = () => {
  const respose = fetch('/api/users/logout').then((res) => res.data);

  return {
    type: SIGN_OUT,
    payload: respose,
  };
};

export const configToken = (getState) => {
  const token = getState().auth.token;

  // Headers
  const config = {
    headers: {
      'Content-type': 'application/json',
    },
  };

  //If Token, add to headers
  if (token) {
    config.headers['end_auth'] = token;
  }

  return config;
};

export const verify = (formValues) => {
  return async (dispatch) => {
    const config = {
      headers: {
        'Content-Type': 'application/json',
      },
    };

    const body = JSON.stringify(formValues);
    console.log(body);
    await Axios.post('/api/users/verify', body, config)
      .then((res) => {
        dispatch(getMessage(res.data.message, 'VERIFICATION_SUCCESS'));
        dispatch({
          type: VERIFICATION_SUCCESS,
          payload: res.data,
        });
      })
      .catch((err) => {
        dispatch(
          returnError(err.response.data.errorMessage, 'VERIFICATION_FAIL')
        );
        dispatch({
          type: VERIFICATION_FAIL,
        });
      });
  };
};

export const otpSend = () => {
  return (dispatch) => {
    Axios.get('/api/users/emailverify')
      .then((res) => {
        console.log(res.data);
        dispatch(getMessage(res.data.message, 'SEND_OTP_SUCCESS'));
        dispatch({
          type: SEND_OTP_SUCCESS,
          payload: res.data,
        });
      })
      .catch((err) => {
        dispatch(returnError(err.response.data.errorMessage, 'SEND_OTP_FAIL'));
        dispatch({
          type: SEND_OTP_FAIL,
        });
      });
  };
};

export const detailUpdater = (formValues) => {
  return (dispatch) => {
    const config = {
      headers: {
        'Content-Type': 'application/json',
      },
    };

    const body = JSON.stringify(formValues);
    if (formValues.password !== formValues.password2) {
      dispatch(returnError('Password did not Match', 'REGISTER_FAIL'));
      dispatch({
        type: REGISTER_FAIL,
      });
    } else {
      Axios.post('/api/users/detials', body, config)
        .then((res) => {
          dispatch(clickedPaymentButton('GET_PAYMENT'));
          dispatch(getMessage(res.data.message, 'DETAIL_UPDATING_SUCCESS'));
          dispatch({
            type: DETAIL_UPDATING_SUCCESS,
            payload: res.data,
          });
        })
        .catch((err) => {
          dispatch(
            returnError(err.response.data.errorMessage, 'DETAIL_UPDATING_FAIL')
          );

          dispatch({
            type: DETAIL_UPDATING_FAIL,
          });
        });
    }
  };
};

export const displayUser = () => {
  return async (dispatch) => {
    await Axios.get('/api/users')
      .then((res) => {
        dispatch(getMessage(res.data.message, 'USERS_LOADING_SUCCESS'));
        dispatch({
          type: USERS_LOADING_SUCCESS,
          payload: res.data,
        });
      })
      .catch((err) => {
        dispatch(
          returnError(err.response.data.errorMessage, 'USERS_LOADING_FAIL')
        );
        dispatch({
          type: USERS_LOADING_FAIL,
        });
      });
  };
};

export const activateUser = (formValues) => {
  return (dispatch) => {
    const config = {
      headers: {
        'Content-Type': 'application/json',
      },
    };

    const body = JSON.stringify(formValues);
    if (formValues.role !== 2) {
      dispatch(
        returnError(
          `${formValues.firstName} is not updated his Detail `,
          'USER_ACTIVATE_FAIL'
        )
      );
      dispatch({
        type: USER_ACTIVATE_FAIL,
      });
    } else {
      Axios.post('/api/users/activate', body, config)
        .then((res) => {
          dispatch(getMessage(res.data.message, 'USER_ACTIVATE_SUCCESS'));
          dispatch({
            type: USER_ACTIVATE_SUCCESS,
            payload: res.data,
          });
        })
        .catch((err) => {
          dispatch(
            returnError(err.response.data.errorMessage, 'USER_ACTIVATE_FAIL')
          );

          dispatch({
            type: USER_ACTIVATE_FAIL,
          });
        });
    }
  };
};
