import { REGISTERSUCCESS, REGISTER_FAIL } from '../actions/types';

const initialState = {
  token: localStorage.getItem('token'),
  isRegistered: false,
  user: null,
  success: null,
  msg: {},
};

export default function (state = initialState, action) {
  switch (action.type) {
    case REGISTERSUCCESS:
      return {
        ...state,
        msg: action.payload.message,
        success: true,
      };
    case REGISTER_FAIL:
      return {
        ...state,
        msg: null,
        success: false,
      };
    default:
      return state;
  }
}
