import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';
import { createBrowserHistory } from 'history';

import { logout } from '../../actions';
import { GoogleAuth } from '../GoogleAuth/GoogleAuth';

export const browserHistory = createBrowserHistory();
export class Header extends Component {
  onLogoutClick = (e) => {
    e.preventDefault();
    this.props.logout();
  };

  static propTypes = {
    auth: PropTypes.object.isRequired,
    logout: PropTypes.func.isRequired,
  };

  adminLink = () => {
    const { role } = this.props.auth.user.user.role;
    if (role === 99)
      return (
        <div>
          <Link
            to='/course/upload'
            className='bg-transparent hover:bg-blue-500 text-blue-700 font-semibold hover:text-white py-2 px-4 border border-blue-500 hover:border-transparent rounded'>
            Upload
          </Link>
        </div>
      );
  };

  render() {
    const { isAuthenticated } = this.props.auth;
    const { name } = '';
    const { user } = this.props.auth;

    const loginLinks = (
      <div>
        <Link
          to='/dashboard'
          className='mr-3 bg-transparent hover:bg-blue-500 text-gray-200 font-semibold hover:text-white py-2 px-4 border border-blue-500 hover:border-transparent rounded'>
          Hi {isAuthenticated ? user.user.firstname : name} !
        </Link>
        <Link
          onClick={this.onLogoutClick}
          to='/logout'
          className='mr-3 bg-transparent hover:bg-blue-500 text-gray-200 font-semibold hover:text-white py-2 px-4 border border-blue-500 hover:border-transparent rounded'>
          Log Out
        </Link>
      </div>
    );

    const guestLinks = (
      <div className='lg:mt-0'>
        <Link
          to='/register'
          className='mr-3 bg-transparent hover:bg-blue-500 text-gray-200 font-semibold hover:text-white py-2 px-4 border border-blue-500 hover:border-transparent rounded'>
          Register
        </Link>
        <Link
          to='/login'
          className='mr-3 bg-transparent hover:bg-blue-500 text-gray-200 font-semibold hover:text-white py-2 px-4 border border-blue-500 hover:border-transparent rounded'>
          Login
        </Link>
      </div>
    );
    return (
      <div className='border-b border-blue-400 bg-gray-800 text-gray-100'>
        <div className='flex p-4 justify-between container mx-auto'>
          <Link to='/' className='mr-3'>
            <h4 className='font-semibold text-xl'>CO-Development</h4>
          </Link>
          {isAuthenticated ? loginLinks : guestLinks}
          {this.adminLink}
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state) => ({
  auth: state.auth,
  user: state.auth.user,
});

export default connect(mapStateToProps, { logout })(Header);
