import {
  ERROR_DELETE,
  ERROR_FETCH,
  ERROR_SHOW,
  REGISTER_FAIL,
  SIGN_IN,
  SIGN_OUT,
  GET_ERROR,
  CLEAR_ERROR,
  REGISTERSUCCESS,
} from './types';

import { returnError } from './errorAction';

import connect from '../routes/connect';
import Axios from 'axios';

export const signIn = (dispatch, getState) => {
  return {
    type: SIGN_IN,
  };
};

export const register = (formValues, history) => {
  return (dispatch) => {
    const config = {
      headers: {
        'Content-Type': 'application/json',
      },
    };

    if (password === repassword) {
      const body = JSON.stringify(formValues);
      Axios.post('http://localhost:5000/api/users/register', body, config)
        .then((res) => {
          dispatch({
            type: REGISTERSUCCESS,
            payload: res.data,
          });
          dispatch(history.push('/login'));
        })
        .catch((err) => {
          dispatch(
            returnError(err.response.data.errorMessage, 'REGISTER_FAIL')
          );
          dispatch({
            type: REGISTER_FAIL,
          });
        });
    } else {
      dispatch(returnError('Password did not Match', 'REGISTER_FAIL'));
      dispatch({ type: REGISTER_FAIL });
    }
  };
};
