import {
  REGISTERSUCCESS,
  REGISTER_FAIL,
  LOGIN_FAIL,
  LOGIN_SUCCESS,
  AUTH_ERROR,
  SIGN_OUT,
  USER_LOADING,
  USER_LOADED,
  GOOGLE_SIGN_IN,
  GOOGLE_SIGN_OUT,
  SEND_OTP_SUCCESS,
  VERIFICATION_SUCCESS,
  VERIFICATION_FAIL,
  SEND_OTP_FAIL,
} from '../actions/types';

const initialState = {
  token: localStorage.getItem('token'),
  isLoading: false,
  isSignedIn: null,
  isAuthenticated: false,
  user: null,
  success: null,
  verified: null,
};

export default function (state = initialState, action) {
  switch (action.type) {
    case USER_LOADING:
      return {
        ...state,
        isLoading: true,
      };
    case USER_LOADED:
      return {
        ...state,
        isLoading: false,
        isAuthenticated: true,
        verified: action.payload,
        user: action.payload,
      };
    case SEND_OTP_SUCCESS:
    case SEND_OTP_FAIL:
    case VERIFICATION_SUCCESS:
      return {
        ...state,
        verified: action.payload,
      };
    case LOGIN_SUCCESS:
    case GOOGLE_SIGN_IN:
    case REGISTERSUCCESS:
      localStorage.setItem('token', action.payload.token);
      return {
        ...state,
        isSignedIn: true,
        user: action.payload,
        verified: action.payload.user.verified,
        isAuthenticated: true,
        isLoading: false,
        success: true,
      };
    case AUTH_ERROR:
    case GOOGLE_SIGN_OUT:
    case SIGN_OUT:
    case LOGIN_FAIL:
    case REGISTER_FAIL:
    case VERIFICATION_FAIL:
      localStorage.removeItem('token');
      return {
        ...state,
        msg: null,
        token: null,
        user: null,
        isSignedIn: false,
        googleUserId: null,
        isAuthenticated: false,
        isLoading: false,
        success: false,
        verified: false,
      };
    default:
      return state;
  }
}
