import React, { Component } from 'react';
import { connect } from 'react-redux';

export class Verify extends Component {
  render() {
    const { mail } = this.props.user;
    return (
      <div>
        <div className='m-6 p-6 container mx-auto h-screen'>
          <h1 className='xl:text-6xl text-center md:text-4xl sm:text-2xl '>
            Verify your account
          </h1>
          <p>A otp is given to your provided mail : </p>
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state) => ({ user: state.auth.user.user });

const mapDispatchToProps = {};

export default connect(mapStateToProps, null)(Verify);
