import {
  SEND_OTP_FAIL,
  VERIFICATION_SUCCESS,
  SEND_OTP_SUCCESS,
  VERIFICATION_FAIL,
} from '../actions/types';

const initialState = {
  isLoading: false,
  isSignedIn: null,
  isAuthenticated: false,
  user: null,
  success: null,
  verified: null,
};

export default function (state = initialState, action) {
  switch (action.type) {
    case SEND_OTP_FAIL:
    case SEND_OTP_SUCCESS:
    case VERIFICATION_SUCCESS:
      return {
        ...state,
        verified: true,
        role: action.payload.user,
      };
    case VERIFICATION_FAIL:
      return {
        ...state,
        verified: false,
        role: action.payload.user,
      };
  }
}
