import React from 'react';
import { loadUser } from '../actions';
import { connect } from 'react-redux';

class Auth {
  constructor() {
    this.authenticated = this.props.auth.isAuthenticated;
  }

  isAuthenticated() {
    loadUser();
    return this.authenticated;
  }
}

const mapStateToProps = (state) => ({
  auth: state.auth,
});

export default new connect(mapStateToProps, null)(Auth());
