import React, { Component } from 'react';
import { displayUser, activateUser } from '../../actions';
import { connect } from 'react-redux';
import Grid from './Sections/Grid';

class RegisteredUser extends Component {
  state = {
    users: [],
    change: false,
  };

  componentDidUpdate() {
    const { isLogin } = this.props.auth;
    if (isLogin) {
      if (!this.state.change) {
        this.setState({
          users: this.props.users.RegisteredUser,
          change: true,
        });
      }
    }
  }

  componentDidMount() {
    this.props.displayUser();
  }

  handleClick = (user) => {
    console.log(user);
    const { firstName, email } = user;
    console.log(this.props);
    this.props.activateUser({ firstName, email });
  };

  render() {
    const { users } = this.state;
    return (
      <div>
        <div className='text-6xl font-ubuntu bg-gray-800 py-64 text-gray-100 text-center'>
          {' '}
          Registered User
        </div>
        <div className='-mt-20 rounded border-2 border-gray-200 container mx-auto bg-white shadow'>
          {users.map((user, data) => (
            <Grid
              firstName={user.firstName}
              lastName={user.lastName}
              email={user.email}
              role={user.role}
              userName={user.userName}
              phoneNumber={user.phoneNumber}
              whatsAppNumber={user.phoneNumber}
              city={user.city}
              state={user.city}
              pinCode={user.pinCode}
              handleClick={this.handleClick(user)}
            />
          ))}
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state) => ({
  auth: state.auth,
  users: state.dashboard.users,
});

export default connect(mapStateToProps, { displayUser, activateUser })(
  RegisteredUser
);
