import { connect } from 'react-redux';

class auth {
  constructor() {
    this.authenticated = uth.isAuthenticated;
  }

  isAuthenticated() {
    return this.authenticated;
  }
}

const mapStateToProps = (state) => ({
  uth: state.auth,
});

export default new connect(mapStateToProps, null)(auth());
