import React, { Component } from 'react';
import PropTypes from 'prop-types';

import Payment from './Payment';
import Detail from './Detail';
import AllProfile from './AllProfile';
import { Link, Route } from 'react-router-dom';
import { connect } from 'react-redux';

export class DashBoard extends Component {
  state = {
    profile: null,
  };
  componentDidUpdate(prevProps) {
    const { button } = this.props;
    console.log(prevProps);
    if (button !== prevProps.button) {
      if (button.id === 'GET_PROFILE') {
        this.setState({ profile: true });
        console.log(this.props);
      }
    }
    console.log(this.props);
    console.log(this.state);
  }

  render() {
    const { profile } = this.state;

    const showDetail = (
      <div>
        <Detail />
      </div>
    );
    const showPayment = (
      <div>
        <Payment />
      </div>
    );
    return (
      <div>
        <div className='container p-4 mx-auto'>
          <div className='grid grid-cols-4 gap-4'>
            <div
              className='col-span-1 flex justify-end pr-4 border-r-2 border-gray-600'
              style={{ height: '100vmin' }}>
              DashBoard
              <AllProfile />
            </div>

            <div className='col-span-3'>{profile && <Detail />}</div>
          </div>
        </div>
      </div>
    );
  }
}

const propTypes = {
  button: PropTypes.object.isRequired,
};

const mapStateToProps = (state) => ({
  button: state.dashBoard,
});

// const mapDispatchToProps = {};

export default connect(mapStateToProps, null)(DashBoard);
