import React, { Component } from 'react';
import { connect } from 'react-redux';

export class Progress extends Component {
  paymentDone = () => {
    return <div className='text-2xl'>Classes will stard on Date</div>;
  };

  paymentNotDone = () => {
    return <div className='text-2xl'>Please Pay</div>;
  };

  render() {
    const { firstName, role } = this.props.user.user.role;
    return (
      <div>
        <div className='text-5xl'>{firstName}</div>
        <div>{role === 3 ? this.paymentDone : this.paymentNotDone}</div>
      </div>
    );
  }
}

const mapStateToProps = (state) => ({
  user: state.auth,
});

export default connect(null, null)(Progress);
