import React from 'react';

function Grid(props) {
  const showDetails = (
    <div>
      Name : {props.firstName} {props.lastName}
      Username : {props.userName}
      Email : {props.email}
      Phone Number : {props.phoneNumber}
      What'sApp Number : {props.whatsAppNumber}
      City : {props.city}
      State : {props.state}
      Pincode : {props.pinCode}
    </div>
  );

  return (
    <div>
      <div className='container mx-auto '>
        <button
          onClick={showDetails}
          className='border-2 flex justify-between w-full p-4 my-4 rounded-lg border-gray-400'>
          <div className='text-2xl'>
            {props.firstName} {props.lastName} has
            {props.role === 2 ? ' Filled the Form' : ' Not Filled the Form'}
          </div>
          <button
            className='ml-6 bg-transparent hover:bg-blue-500 text-blue-700 font-semibold hover:text-white py-2 px-4 border border-blue-500 hover:border-transparent rounded'
            onClick={props.handleClick}>
            Activate
          </button>
        </button>
      </div>
    </div>
  );
}

export default Grid;
