import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';

import { googleSignIn, googleSignOut } from '../../actions';

export class GoogleAuth extends Component {
  componentDidMount() {
    window.gapi.load('client:auth2', () => {
      window.gapi.client
        .init({
          clientId:
            '895322340657-oubpcqc5do5aerdr7gv828q0sbsdjila.apps.googleusercontent.com',
          scope: 'email',
        })
        .then(() => {
          this.auth = window.gapi.auth2.getAuthInstance();
          this.onAuthChange(this.auth.isAuthenticated.get());
          this.auth.isAuthenticated.listen(this.onAuthChange);
        });
    });
  }

  onAuthChange = (isAuthenticated) => {
    if (isAuthenticated) {
      this.props.googleSignIn(this.auth.currentUser.get().getId());
    } else {
      this.props.googleSignOut();
    }
  };

  onSignInClick = () => {
    this.auth.googleSignIn();
  };

  onSignOutClick = () => {
    this.auth.googleSignOut();
  };

  renderAuthButton() {
    if (this.props.isSignedIn === null) {
      return null;
    } else if (this.props.isSignedIn) {
      return (
        <div>
          <button
            onClick={this.onSignOutClick}
            className='ui red google button'>
            <i className='google icon' />
            Sign Out
          </button>
        </div>
      );
    } else {
      return (
        <div>
          <button onClick={this.onSignInClick} className='ui red google button'>
            <i className='google icon' />
            Sign In
          </button>
        </div>
      );
    }
  }

  render() {
    return <div>{this.renderAuthButton()}</div>;
  }
}

const mapStateToProps = (state) => ({
  isAuthenticated: state.auth.isAuthenticated,
});

const mapDispatchToProps = { googleSignIn, googleSignOut };

export default connect(mapStateToProps, mapDispatchToProps)(GoogleAuth);
