import React, { Component } from 'react';
import PropTypes from 'prop-types';

import Payment from './Payment';
import Detail from './Detail';
import AllProfile from './AllProfile';
import { Link, Route } from 'react-router-dom';
import { connect } from 'react-redux';
import NotFound from '../404/NotFound';
import { Verify } from '../Verify/Verify';
import { otpSend, verify } from '../../actions';
export class DashBoard extends Component {
  profileRender() {
    return (
      <div className='grid grid-cols-4 gap-4'>
        <div
          className='col-span-1 flex justify-end pr-4 border-r-2 border-gray-600'
          style={{ height: '100vmin' }}>
          DashBoard
          <AllProfile />
        </div>

        <div className='col-span-3'>
          {this.props.button.id === 'GET_PROFILE' && <Detail />}
          {this.props.button.id === 'GET_PAYMENT' && <Payment />}
        </div>
      </div>
    );
  }

  verification = () => {
    this.props.otpSend();
  };

  render() {
    if (!this.props.isAuth) {
      return <NotFound />;
    }
    if (!this.props.verified) {
      console.log(this.props);
      this.props.otpSend();
      return (
        <div>
          <Verify {...this.props} />
        </div>
      );
    }
    return (
      <div>
        <div className='container p-4 mx-auto'>{this.profileRender()}</div>
      </div>
    );
  }
}

const propTypes = {
  button: PropTypes.object.isRequired,
};

const mapStateToProps = (state) => ({
  button: state.dashboard,
  isAuth: state.auth.isAuthenticated,
  verified: state.auth.verified,
});

// const mapDispatchToProps = {};

export default connect(mapStateToProps, { otpSend, verify })(DashBoard);
