import { REGISTERSUCCESS } from '../actions/types';

const initialState = {
  user: null,
};

export default function (state = initialState, action) {
  switch (action.type) {
    case REGISTERSUCCESS:
      return {
        ...state,
        ...action.payload,
      };
    default:
      return state;
  }
}
