import React, { Component } from 'react';
import { connect } from 'react-redux';

export class Progress extends Component {
  render() {
    console.log(this.props);
    const payed = (
      <div className='text-2xl text-center'>Classes will stard on Date</div>
    );

    const notPayed = <div className='text-2xl text-center'>Not Yet Payed</div>;

    return (
      <div>
        <div className='text-5xl'>{this.props.user.user.firstname}</div>
        <div>{this.props.user.user.role === 3 ? payed : notPayed}</div>
      </div>
    );
  }
}

const mapStateToProps = (state) => ({
  user: state.auth.user,
});

export default connect(mapStateToProps, null)(Progress);
